package main

import (
	"fmt"
)

var X map[string]int

func ladderLength(beginWord string, endWord string, wordList []string) int {
	M := make(map[string][]string)
	X = make(map[string]int)
	wordList = append(wordList, beginWord)
	for i := 0; i < len(wordList); i++ {
		for j := 0; j < len(wordList); j++ {
			if oneLetterDiff(wordList[i], wordList[j]) {
				if _, ok := M[wordList[i]]; !ok {
					M[wordList[i]] = make([]string, 0)
				}
				M[wordList[i]] = append(M[wordList[i]], wordList[j])
			}
		}
	}
	X[endWord] = 1
	used := make(map[string]bool)
	d(used, M, beginWord)

	// fmt.Println(X)

	XX := make(map[int][]string)
	for k, v := range X {
		if XX[v] == nil {
			XX[v] = make([]string, 0)
		}
		XX[v] = append(XX[v], k)
	}

	// fmt.Println(XX)
	println("")
	for k, v := range XX {
		print(k)
		fmt.Println(v)
	}
	return X[beginWord]
}

func d(used map[string]bool, M map[string][]string, beginWord string) int {
	if r, ok := X[beginWord]; ok {
		return r
	}
	res := make([]int, 0)
	for _, word := range M[beginWord] {
		if used[word] {
			continue
		}
		used[word] = true
		res = append(res, d(used, M, word))
		used[word] = false
	}
	min := 0
	for _, r := range res {
		if (r != 0 && min == 0) || (min > r && r != 0) {
			min = r
		}
	}
	if min == 0 {
		X[beginWord] = 0
	} else {
		X[beginWord] = min + 1
	}
	return X[beginWord]
}

func oneLetterDiff(a, b string) bool {
	count := 0
	for i := 0; i < len(a); i++ {
		if a[i] != b[i] {
			count++
		}
		if count > 1 {
			return false
		}
	}
	return count == 1
}

